    var geometry_plane = new THREE.BoxGeometry( 40, 0.1, 40);  
    var texture_plane = THREE.ImageUtils.loadTexture('../public/images/snow.jpg', {}, function() {
    renderer.render(scene, camera);
    })
    //var material_plane = new THREE.MeshBasicMaterial({map: texture_plane, transparent: true })
