    scene.add( plane );*/

    
    // instantiate the loader
    let loader = new OBJLoader();

    // function called on successful load
    function callbackOnLoad ( object3d ) {
        scene.add( object3d );
    }

    /*var mtlLoader = new MTLLoader();
    mtlLoader.setTexturePath('/src/components/assets/');
    mtlLoader.setPath('/src/components/assets/');
    mtlLoader.load('13884_UFO_Saucer_v1_l2.mtl', function (materials) {

        materials.preload();


        var objLoader = new OBJLoader();
        objLoader.setMaterials(materials);
        objLoader.setPath('/src/components/assets/');
        objLoader.load('13884_UFO_Saucer_v1_l2.obj', function (object) {

            scene.add(object);
            object.position.y -= 60;

        }); 

    });//*/

    var objLoader = new OBJLoader();
    objLoader.setPath('/src/components/assets');
    objLoader.load('13884_UFO_Saucer_v1_l2', function (object) {

        scene.add(object);
        //object.position.y -= 0;
        object.position.set(0, 0, 0)

    });

    var animate = function () {
      requestAnimationFrame( animate );
      //cube.rotation.x += 0.01;
      //cube.rotation.y += 0.001;

      //cube.position.x += 0.01;
      renderer.render( scene, camera );
    };
    animate();
    // === THREE.JS EXAMPLE CODE END ===
    
    return (<div><h1>Cubo</h1></div>)
}

export default App;
